args:
echo "/* Sentence Block Styles */
.sentence-block {
    font-size: 0.9rem;
    line-height: 1.4;
}
.sentence-block .cn {
    font-family: var(--font-cn);
    font-size: 1.1rem;
    color: var(--text-dark);
}
.sentence-block .py {
    color: var(--theme-primary);
    font-style: italic;
    font-size: 0.85rem;
}
.sentence-block .en {
    color: var(--text-light);
    font-size: 0.85rem;
}
.hanzi-main {
    font-family: var(--font-cn);
    font-size: 1.4rem;
    font-weight: bold;
}" >> style.css

echo "
/* Chapter Header in Table */
.chapter-header td {
    background-color: var(--theme-bg-alpha);
    color: var(--theme-primary-dark);
    font-weight: 700;
    text-align: center;
    font-size: 1.1rem;
    padding: 15px;
    border-top: 2px solid var(--theme-primary);
}
" >> style.css
